import {Component, ViewChild, ElementRef, OnInit} from '@angular/core';
import * as jsPDF from 'jspdf';

@Component({
  selector: 'app-generar-jspdf',
  templateUrl: './generar-jspdf.component.html',
  styleUrls: ['./generar-jspdf.component.scss']
})
export class GenerarJspdfComponent {
  name = 'FICHA DE INSPECCION PRINCIPAL GENERAL';
  pdfSrc = '';

  @ViewChild('pdfTable', {static: false}) pdfTable: ElementRef;

  public downloadAsPDF(cad: string) {
    const doc = new jsPDF();

    const specialElementHandlers = {
      '#editor': function (element, renderer) {
        return true;
      }
    };

    const pdfTable = this.pdfTable.nativeElement;

    doc.fromHTML(pdfTable.innerHTML, 15, 15, {
      width: 190,
      'elementHandlers': specialElementHandlers
    });

    if (cad === 'descargar') {
      doc.save('tableToPdf.pdf');
    } else if (cad === 'generar') {
      let blobPDF = new Blob([doc.output()], {type: 'application/pdf'});
      this.pdfSrc = URL.createObjectURL(blobPDF);
      console.log('blobUrl', this.pdfSrc);
    }
  }

  informePDF(cad: string) {
    var getImageFromUrl = function(url) {
      var img = new Image();
      img.onerror = function() {
        alert('Cannot load image: "' + url + '"');
      };
      img.onload = function() {
        console.log('image is loaded');
      };
      return img.src = url;
    }

    var imgData = getImageFromUrl('data:image/png;base64,');

    var doc = new jsPDF('p', 'pt', 'a4');
    var width = doc.internal.pageSize.width;
    var height = doc.internal.pageSize.height;
    var options = {
      pagesplit: true
    };
    doc.text(10, 20, 'Crazy Monkey Informaci√≥n');
    var h1=50;
    var aspectwidth1= (height-h1)*(9/16);
    doc.addImage(imgData, 'JPEG', 10, h1, aspectwidth1, (height-h1), 'monkey');
    doc.addPage();
    doc.text(10, 20, 'Hello World');
    var h2=30;
    var aspectwidth2= (height-h2)*(9/16);
    doc.addImage(imgData, 'JPEG', 10, h2, aspectwidth2, (height-h2), 'monkey');

    if (cad === 'descargar') {
      doc.save('file.pdf'); // Save the PDF
    } else if (cad === 'generar') {
      this.pdfSrc = doc.output('bloburl', 'file.pdf');
      console.log('blobUrl', this.pdfSrc);
    }

  }
}
